using Microsoft.EntityFrameworkCore;
using Microsoft.EntityFrameworkCore.Metadata.Builders;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;
using YemekTarifiApp.Entity;
using YemekTarifiApp.Entity.Concrate;

namespace YemekTarifiApp.Data.Config
{
    public class RecipeConfig : IEntityTypeConfiguration<Recipe>
    {
        public void Configure(EntityTypeBuilder<Recipe> builder)
        {
            builder.HasKey(r => r.Id);
            builder.Property(r => r.Id).ValueGeneratedOnAdd();


            builder.Property(r => r.Owner).ValueGeneratedOnAdd()
                .IsRequired()
                .HasMaxLength(100);

            builder.Property(r => r.OwnerMail).ValueGeneratedOnAdd()
                .IsRequired()
                .HasMaxLength(100);

            builder.Property(r => r.RecipeMaking)
                .IsRequired()
                .HasMaxLength(1000);




            builder.ToTable("Repices");
            builder.HasData(

              new Recipe { Id = 1, Owner="Ali" , OwnerMail="ali@gmail.com",  RecipeMaking = "Büyük boy tencerenizi ocağa alın ve ısıtın.\r\nİçerisine zeytinyağı ve yemeklik doğradığınız soğanı ilave edip sotelemeye başlayın.\r\nKüçük küp doğradığınız patatesi ilave edip karıştırın.\r\nKüçük küp doğradığınız havuç, kırmızı kapya biber ve dilimlediğiniz kereviz sapını ilave edip karıştırın.\r\nİnce doğradığınız sarımsak, domates püresi ve rendelenmiş pancarı ilave edip karıştırın.\r\nDefne yaprağı, beyaz sirke, tuz ve karabiberi ilave edip karıştırın.\r\nEt suyu ve suyu ilave edip karıştırın.\r\nKapağını kapatın ve 40 – 45 dakika pişmeye bırakın.\r\nPişen çorbanızı servis kaselerine alıp üzerine krema ve ince kıyılmış taze soğan serpiştirip servis edin." },
              new Recipe { Id = 2, Owner = "Ahmet", OwnerMail = "ahmet@gmail.com", RecipeMaking = "Çorba tencerenizi ocağa alın ve zeytinyağını ilave edip ısıtın.\r\nİnce yemeklik doğradığınız soğanı ilave edip yumuşayana kadar kavurun.\r\nUnu ilave edin ve kokusu çıkana kadar kavurun.\r\nYıkayıp suyunu süzdüğünüz mercimek, tuz ve karabiberi ilave edip karıştırın.\r\nSuyunu ilave edip mercimekler iyice yumuşayana kadar 30 dakika pişirin.\r\nMercimekler pişince tuzunu ilave edin ve bir blender yardımıyla çorbanızı pürüzsüz bir kıvam alana kadar çekin.\r\nBir karıştırma kabında yumurta sarısı, süt ve limon suyunu karıştırın.\r\nKarışımın üzerine bir kepçe çorba alın ve karıştırın.\r\nArdından çorba tenceresine ekleyin ve karıştırın.\r\nBir tavanın içerisine tereyağını alın ve eritin.\r\nEriyen yağın içerisine kuru nane, toz kırmızı biber ve pul biberi ilave edip karıştırıp çorbanızın içerisine ekleyin.\r\nSıcak servis edin." },
              new Recipe { Id = 3, Owner = "Zeynep", OwnerMail = "zeynep@gmail.com", RecipeMaking = "Tereyağını tencereye alıp eritin.\r\nİnce yemeklik doğradığınız soğan ve ezilmiş sarımsakları tencerenize alıp yumuşayana kadar soteleyin.\r\nYıkanmış kırmızı mercimek, bulgur ve pirinci tencerenize ilave edip birkaç tur karıştırın.\r\nTuz, karabiber ve suyunu ilave edip birkaç tur karıştırın.\r\nTencerenizin kapağını kapatın ve pişmeye bırakın.\r\n\r\nKruton için;\r\nRamazan pidenizi küçük küpler olacak şekilde kesin.\r\nTavanızı ocağa alın ve ısıtın.\r\nIsınan tavanızın içerisine tereyağını ilave edip eritin.\r\nEriyen tereyağın içerisine kestiğiniz pideleri alın ve soteleyin.\r\nÇıtırlaşana pideleri kenara alın ve soğutun.\r\n\r\nMeyanesi için;\r\nBir sos tenceresine tereyağını ekleyin ve eritin.\r\nYağınız eriyince domates salçası ve unu ilave edip kavurun.\r\nKuru nane ve pul biberi ilave edip karıştırın.\r\nSuyunu ilave edin ve kıvam alana kadar karıştırın.\r\n\r\nBirleştirmek için;\r\nÇorba tencerenizdeki bakliyatlar pişince meyanesini ilave edip karıştırın.\r\nKapağını kapatın ve 10 – 15 dakika daha pişmeye bırakın.\r\nÇorbanızı kaselere pay edin.\r\nÜzerine krutonlardan serpiştirin ve sıcak servis edin." },
              new Recipe { Id = 4, Owner = "Tunay", OwnerMail = "tunay@gmail.com", RecipeMaking = "Tencerenizi ocağa alın ve ısıtın.\r\nTereyağını içerisine alın ve eritin.\r\nSoğanı iri küp olacak şekilde doğrayın ve tencereye alıp yumuşayana kadar soteleyin.\r\nİri küp doğradığınız patatesleri ilave edip karıştırın.\r\nKüp doğradığınız balkabaklarını ilave edin ve karıştırın.\r\nMuskat rendesi, yıldız anason, tuz ve karabiberi ilave edip karıştırın.\r\nArdından et veya sebze suyunu ilave edip karıştırın.\r\nKapağını kapatın ve sebzeler pişene kadar su kaynayana kadar yüksek, kaynadıktan sonra kısık ateşte pişirmeye devam edin.\r\nSebzeler piştikten sonra ocağın altını kapatın ve yıldız anasonu tencerenin içinden çıkarın.\r\nBlenderden geçirin ve 2 yemek kaşığı kremayı içerisine ekleyerek karıştırın.\r\nÇorbanızı servis kaselerine alın ve sıcak servis edin." },
              new Recipe { Id = 5, Owner = "Tuncay", OwnerMail = "tuncay@gmail.com", RecipeMaking = "Çorba tencerenizi ocağa alın ve ayçiçek yağını ilave edip ısıtın.\r\nİnce yemeklik doğradığınız soğanı ilave edip yumuşayana kadar soteleyin.\r\nEzilmiş sarımsak ve küçük küp doğradığınız patatesi ilave edip kavurun.\r\nTavuk suyunu ilave edip 10 dakika kaynatın.\r\nBezelye, taze nane yaprakları, tuz ve karabiberi ilave edip 10 dakika daha kaynatın.\r\nHer şey yumuşayınca bir el blenderi yardımıyla pürüzsüz olana kadar çekin.\r\nHaşlayıp didiklediğiniz tavuğu ilave edin ve 5 dakika daha kaynatın.\r\nHazır olan çorbanızı servis kaselerine pay edin ve taze nane yaprağıyla süsleyerek servis edin." },
              new Recipe { Id = 6, Owner = "Serhat", OwnerMail = "serhat@gmail.com", RecipeMaking = "Bir karıştırma kabına kıyma, panko, süt, soğan, sarımsak tozu, yenibahar, tuz ve karabiberi alıp iyice yoğurun.\r\nKöftelerinizi 45’er gramlık köfteler alın ve yuvarlayarak köfte şekline getirin.\r\nTavanızı ocağa alın ve ısıtın.\r\nIsınan tavanızın içerisine köfteleri alın ve 7 – 8 dakika kızarana kadar pişirin.\r\nKenarından tereyağı koyun ve bir tur karıştırıp bir tabağa çıkartın.\r\nAynı tavanın içerisine tereyağını ilave edip eritin.\r\nUnu ilave edip kavurun.\r\nKavrulan unun içerisine et suyu ve kremayı ilave edip karıştırın.\r\nArdından hardal, tuz ve karabiberi ilave edip karıştırın.\r\nHazır olan sosun içerisine köfteleri ekleyin ve her taraflarını sosa bulayın.\r\nKöftelerin üzerine dilimlediğiniz bayat ekmekleri dizin.\r\nÜzerine rendelenmiş kaşar peyniri serpiştirin ve 180 derecede önceden ısıtılmış fırında peynirler eriyene kadar pişirin.\r\nFırından çıkardığınız köftelerin üzerine ince kıyılmış maydanoz ekleyin ve sıcak servis edin." },
              new Recipe { Id = 7, Owner = "Tugay", OwnerMail = "tugay@gmail.com", RecipeMaking = "1 sıcak su dolu bardağa 1 tutam tel safran atın ve safranın renk vermesi için kenara alın.\r\nİri kuşbaşı doğradığınız etleri bir karıştırma kabına alın.\r\nİçerisine un, tuz ve karabiberi ekleyin.\r\nUn, etin her yerine kadar elinizle karıştırın.\r\n\r\nTencerenizi ocağa alın ve zeytinyağı ilave edip ısıtın.\r\nIsınan yağın içerisine etleri ekleyin ve bir tarafı altın rengi alana kadar pişirin.\r\nEtlerinizin bir tarafı pişince diğer tarafını çevirin ve pişirmeye devam edin.\r\nEtler iyice renk alınca piyazlık doğradığınız soğan ve ince verev dilimlediğiniz yeşil biberi ilave edip pişirmeye devam edin.\r\nRendelenmiş zencefil, tuz ve karabiberi ilave edip karıştırın.\r\nSafranlı su ve et suyunu ilave edip yüksek ateşte kaynamaya bırakın.\r\nTenceredeki et suyu kaynayınca altını kısın ve kısık ateşte tencerenin ağzı kapalı olacak şekilde 1 saat pişirin.\r\n\r\n1 saat sonra tencerenin kapağını açın ve bir tur karıştırın.\r\nLimon suyu, havanda dövdüğünüz kişniş, ortadan ikiye kestiğiniz kuru kayısı ve ince verev dilimlediğiniz yeşil biberi ilave edip 15 dakika daha pişirin.\r\nPişen etinizi 10 dakika dinlendirin ve sıcak servis edin." },
              new Recipe { Id = 8, Owner = "Berkay", OwnerMail = "berkay@gmail.com", RecipeMaking = "Soğanı küp şeklinde doğrayıp kıyma ile birlikte tavaya alın. Üzerine zeytinyağı gezdirip orta ateşte kavrulmaya bırakın.\r\nDomatesleri mutfak robotunda püre haline getirip kavrulmuş kıymaya ekleyin. Salça ve biber sosunu ilave edin.\r\nKısık ateşte suyunu çekinceye kadar pişirin. Biberleri küp şeklinde doğrayıp ayrı bir tavada az zeytinyağı ile soteleyin.\r\nKıymaya ekleyin. Tuz ve baharatlarla tatlandırın. Birkaç dakika pişirip ocaktan alın. Taco kabuklarını fırında ısıtın.\r\nKıyma karışımını içlerine doldurun ve üzerlerine kıyılmış maydanoz serpiştirerek servis yapın. Afiyet olsun." },
              new Recipe { Id = 9, Owner = "Berkcan", OwnerMail = "berkcan@gmail.com", RecipeMaking = "Düdüklü tencerenizi ocağa alın ve ısıtın.\r\nTereyağı ve zeytinyağı ilave edip tereyağını eritin.\r\nİri kuşbaşı doğradığınız etleri ilave edip birkaç tur karıştırın.\r\nArpacık soğanları ilave edip kavurun.\r\nDomates salçası ve unu ilave edip kokuları çıkana kadar kavurun.\r\nTuz, karabiber, taze kekik, havanda dövülmüş tane kişniş, nar ekşisi, su ve defne yaprağını ilave edip karıştırın.\r\nTencerenizin kapağını kapatın ve 30 – 40 dakika pişirin.\r\nPişen etlerinizi ocaktan alın ve içerisine nar tanelerini ilave edip karıştırın.\r\nServis ederken kalan nar tanesi ve taze soğanın yeşil kısımlarını ince kıyıp süsleyin.\r\nSıcak servis edin." },
              new Recipe { Id = 10, Owner = "Hasan", OwnerMail = "hasan@gmail.com", RecipeMaking = "Marinasyon sosunun tüm malzemeleri güzelce karıştırıp etleri bu sosla 1 gece önceden ovup 1 gün kadar buz dolabında sosun içinde bekletin.\r\nZamansız yoksa 3-4 saat de yeterli olacaktır.Zeytinyağını tavaya alıp kızdırın.\r\nİçerisine piyazlık doğranmış soğan ile julyen doğranmış biberleri ekleyin ve çok öldürmeden pişirin.\r\nHafif diri kalmaları gerekiyor. Ocaktan almaya yakın soya sosunu ekleyip bir iki kez çevirdikten sonra altını kapatın.(Ben sebzeleri diri seviyorum çok kısa süre ocakta pişirdim. )\r\nAyrı bir tavayı çok iyi ısıtın. Etleri ısınan tavaya alıp mühürleyin ve yüksek ateşte ara ara ters yüz ederek çok kurutmadan pişirin.\r\nEtler pişerken tadını alması için tortilla ekmeklerini üstüne bastırabilirsiniz.\r\nEn son etleri sebzeler ile harmanlayın ve sıcak sıcak servis edin. Deneyecek olanlara şimdidenafiyet olsun" },
              new Recipe { Id = 11, Owner = "Aygül", OwnerMail = "aygul@gmail.com", RecipeMaking = "Parçalayıcınızın haznesine küp doğradığınız tavuk etini alın ve çekmeye başlayın.\r\nYumurtaları ilave edip çekmeye devam edin.\r\nUnu ilave edip hafif katı bir harç elde edin.\r\nZeytinyağı, tuz ve karabiberi ilave edip iyice çekin.\r\nNOT : Hafif ele yapışan bir harç elde edeceksiniz.\r\nBir kasenin içerisine su ilave edin ve elinizi ıslatarak hazırladığınız harçtan ceviz büyüklüğünde parçalar kopartıp dikdörtgen şekil verin.\r\nGeniş bir tavanın içerisine ayçiçek yağı koyup ısınmaya bırakın.\r\nUn, galeta unu ve yumurtayı ayrı ayrı tabaklara koyun.\r\nHazırladığınız tavukları sırasıyla un, yumurta ve galeta ununa bulayın.\r\nIsınan yağa alın ve arkalı önlü kızartın.\r\nKızaran nuggetleri havlu kağıt serdiğiniz tabağa alın ve fazla yağını bırakmasını sağladıktan sonra servis edin." },
              new Recipe { Id = 12, Owner = "Nesrin", OwnerMail = "nesrin@gmail.com", RecipeMaking = "Tavanızı ocağa alın ve zeytinyağı ilave edip ısıtın.\r\nIsınan tavanızın içerisine iri küp doğradığınız patlıcan, kabak ve patatesi ilave edip soteleyin.\r\nSotelenen sebzeleri bir tabağa çıkartın.\r\nBir karıştırma kabının içerisine zeytinyağı, soya sos, domates salçası, biber salçası, tuz, karabiber ve kuru kekiği ilave edip bir çırpma teli yardımıyla karıştırın.\r\nİri küp doğradığınız tavukları sosun içerisine alın ve iyice harmanlayın.\r\nTavukları sosuyla beraber sebzeleri sotelediğiniz tavaya alın ve soteleyin.\r\nSotelenen tavukların içerisine arpacık soğan, bezelye, sarımsak ve çekirdekleri çıkartılmış zeytini ilave edip karıştırın.\r\nHazır olan tavuklu karışımı sebzeli karışıma ekleyin ve harmanlayın.\r\nİri dövülmüş cevizi ilave edip karıştırın.\r\nDikdörtgen kestiğiniz fırın kağıdını tezgaha koyun.\r\nFırın kağıdının ortasına hazırladığınız iç harçtan koyun.\r\nİki geniş kenarından katlayın ve kısa kenarlarını altına doğru kıvırarak fırın tepsisine dizin.\r\n180 derecede önceden ısıtılmış fırında 30 dakika pişirin.\r\nHazırladığınız tavuğu 5 – 10 dakika dinlendirin ve sıcak servis edin." },
              new Recipe { Id = 13, Owner = "Neslihan", OwnerMail = "neslihan@gmail.com", RecipeMaking = "Tavuk için;\r\nTencerenizi ocağa alın.\r\nSu, tane karabiber, dörde bölünmüş soğan, kereviz sapı, tavuk baget ve tuzu ilave edip tavuklar haşlanana kadar pişirin.\r\nPişen tavuklarınızı didikleyin ve bir karıştırma kabının içerisine alın.\r\nİçerisine haşlanmış nohut ve birkaç kaşık tavuk suyundan ekleyip karıştırın.\r\n\r\nTabanı için;\r\nTavanızı ocağa alın ve ısıtın.\r\nTereyağını ilave edip eritin ve küp doğradığınız pideleri ekleyip kavurun.\r\n\r\nDomates sosu için;\r\nBir sos tenceresinin içerisine zeytinyağını ilave edip ısıtın.\r\nİnce yemeklik doğradığınız soğanı ilave edip yumuşayana kadar soteleyin.\r\nSalçayı ilave edip kavurun.\r\nTavuğu haşladığınız sudan ilave edin ve karıştırarak salçayı açın.\r\nRendelediğiniz domatesi ilave edip karıştırın.\r\nKuru nane, kuru kekik, tuz ve karabiberi ilave edip karıştırın\r\n10 dakika kaynamaya bırakın.\r\n\r\nYoğurt için;\r\nBir karıştırma kabının içerisine süzme yoğurt ve ezilmiş sarımsağı alıp karıştırın.\r\n\r\nBirleştirmek için;\r\nPidelerinizi servis tabağınızın tabanına dizin.\r\nÜzerine hazırladığınız domates sosunu gezdirin.\r\nDomates sosunun üzerine hazırladığınız yoğurdu gezdirin.\r\nYoğurdun üzerine didiklediğiniz tavukları koyun.\r\nÜzerine kuru nane serperek servis edin." },
              new Recipe { Id = 14, Owner = "Fatma", OwnerMail = "fatma@gmail.com", RecipeMaking = "Patlıcanı alacalı soyarak küçük küçük doğrayın ve sıvı yağ ile kızartın.\r\nSüzgece alarak yağının süzmesi bekleyin bu arada tavukları soteleyin.\r\nTavukları kuş başı doğrayın, tencereye alarak arada karıştırarak pişirin.\r\nRengi dönüp suyunu saldığında yemeklik doğranmış soğanı ekleyin.\r\nSoğanlar yumuşadıktan sonra salçayı ve isteğe bağlı kullanacaksanız eğer ketçabı ilave edin.\r\nKapağını kapatarak 4-5 dk. kadar pişmeye bırakın.\r\nSuyunu çektikten sonra bezelye ve patlıcanı ilave edin.\r\nBaharatları da ekleyerek 3-4 dk. daha pişirin.\r\nTavuklar da artık pişmiş olmalılar.\r\nBeşamel sos için, tereyağını eritin ve unu kokusu çıkana kadar kavurun.\r\nÜzerine sütü ilave ederek çırpıcı yardımı ile topak kalmayacak şekilde koyulaşıp göz göz olana kadar karıştırarak pişirin.\r\nSon olarak tuzunu ilave ederek ocaktan alın.\r\nKüçük bir kaseye 4’e böldüğünüz yufkadan serin.\r\nYufkanın kenarlarından parçalar kopartarak çukur olan kısma bir kat daha serin.\r\nKaseye tavuklu harçtan koyarak, kasenin kenarlarından sarkan yufkaları üzerine kapatın.\r\nYağlanmış fırın kabına ters çevirin.\r\nTüm malzemeyi bu şekilde yaptıktan sonra üzerleri beşamel sostan her birine eşit miktarda olacak şekilde dökün.\r\n190 derece fırında yufkalar pembeleşene kadar pişirin.\r\nSonra üzerine kaşar peyniri rendesi serpin ve tekrar fırına sürün.\r\nPeynirler eriyip kızardığında alabilirsiniz.\r\nNot: Sadece tavuk göğsü ile de yapabilirsiniz ama ben göğüs eti kuru olduğu için sote yaparken ya pirzola kullanıyorum ya da bu şekilde karıştırıyorum." },
              new Recipe { Id = 15, Owner = "Arda", OwnerMail = "arda@gmail.com", RecipeMaking = "Öncelikle iç pilavını hazırlamak için pilavlık pirinçler bir kaba alınır.\r\nÜzerini geçecek kadar ılık su ve tuz eklenerek 20 dakika bekletilir. Daha sonra bol su ile yıkanarak pişirme aşamasına geçilir.\r\nPilavı pişirmek için öncelikle pilav tenceresine tereyağı ve sıvı yağ alınır. Ben tereyağı ve sıvı yağı karışık kullanıyorum, çünkü hem tereyağının lezzetini seviyorum, hem de sıvı yağın parlaklık verdiğini düşünüyorum.\r\nYağlar eriyince suyu süzülen pirinçler eklenerek kavrulur.\r\n2-3 dakika kavrulduktan sonra dolmalık fıstıklar ve kuş üzümleri ilave edilir. Beraber bir iki defa karıştırılarak kavrulur.\r\nÜzerine 2 su bardağı sıcak su ilave edilir. Tuzu ayarlanır. Tencerenin kapağı kapatılarak pişmeye bırakılır.\r\nPilav suyunu çekinceye kadar pişirilir. Bu ölçüdeki pirinç için suyu bilerek az kullanıyoruz, pilavımız biraz diri kalacak.\r\nSon olarak ocaktan alınır ve tarçın ve karabiber eklenerek nazikçe karıştırılır.\r\nDiğer taraftan bütün tavuğumuz yıkanır ve varsa tüyleri ütülenir. Bu tarifte organik tavuk kullandım. Eğer bulabilirseniz köy tavuğu daha geç pişer ama daha sağlıklı ve lezzetli olacaktır, tavsiye ederim.\r\nTavuğun içi fazla bastırmadan pilavla doldurulur. Çok sıkıştırırsanız pilavınız hamur olabilir.\r\nTavuğun ayakları ya derisinden geçirilerek sıkıştırılır veya mutfak ipi ile bağlanır, böylece pişerken açılması önlenmiş olur.\r\nBir kasede sos malzemeleri karıştırılır ve fırça ile tavuğun her yerine gelecek şekilde sürülür.\r\nFırın torbasına un alınır. Poşet çevrilip sallanarak unun her yerine gelmesi sağlanır.\r\nArdından sosladığımız tavuğu poşetin içerisine dikkatli bir şekilde yerleştirelim. Garnitür olarak tavuğun yanına arpacık soğan, patates, havuç, ince kesilmiş limon dilimleri ve birkaç yaprak defne kullandım.\r\nPoşetin ağzını kapattıktan sonra bir kürdanla poşetin üzerine delikler açalım.\r\nTavuğumuzu önceden 190 dereceye ısıttığımız fırında 60 dakika boyunca pişmeye bırakalım.\r\nTencerede kalan pilava sıcak su ilave ederek pişirmeye devam edelim.  Tekrar suyunu çektiğinde üzerine bir havlu kağıt kapatarak  demlenmeye bırakılır.\r\n60 dakikanın sonunda tavuğu fırından alalım. Üzerindeki poşeti açarak fanlı ayarda 10 dakika daha pişirelim. Sürenin sonunda nar gibi kızaran nefis tavuk dolmamız servise hazır.\r\nİç pilavlı tavuk dolması nasıl servis edilir? Ben genişçe bir servis tabağının tabanına demini alan pilavı yayarak tavuk için bir yatak oluşturdum ve fırından çıkan tavuğu pilavın ortasına yerleştirdim. Pişen sebzelerimi de gelişigüzel pilavın üzerine serpiştirdim, tercih sizin, şimdiden afiyet olsun." },
              new Recipe { Id = 16, Owner = "Eyşan", OwnerMail = "eysan@gmail.com", RecipeMaking = "Taze soğan maydanoz ve taze naneyi bıçak yardımı ile iyice ince ince kıyın. Karanfil ve susamı havanda dövün. Çok kuru olmaması için içerisine 1 yemek kaşığı zeytinyağı ekleyin. İyice kıydığınız taze otları tuz, karabiber ve havanda dövdüğünüz karanfil susam karışımı ile lezzetlendirin.\r\n\r\nTemizlenmiş ve kılçıklarından arındırılmış hamsilerin birinin üzerine bu hazırladığınız harçtan sürüp diğer bir hamsiyi üzerine kapatın.\r\n\r\nTüm hamsileri bu şekilde hazırladıktan sonra mısır ununa bulayıp bir tavada kızdırdığınız sığ yağda kızartın. Dilerseniz hamsilerin içlerini boş bırakıp, sadece tuz karabiber ile tatlandırarak da kızartabilirsiniz.\r\n\r\nKızarttığınız hamsileri havlu peçete olan bir tabağa aktarın ve fazla yağından arındırıp, roka, limon ve soğan ile servis edin." },
              new Recipe { Id = 17, Owner = "Junior", OwnerMail = "junior@gmail.com", RecipeMaking = "Somon için;\r\nTavanızı ocağa alın ve ekmek kırıntısını içerisine alıp rengi hafif dönene kadar kavurun.\r\nİçerisine tereyağını ilave edip eritin.\r\nArdından ince kıyılmış sarımsak ve fesleğeni ilave edip karıştırın.\r\n\r\nSomonlarınızın kenar kısmında kalan hafif yağlı kısımlarını bıçağınızla kesin.\r\nAma derisi üzerinde kalsın.\r\nBir tavayı ocağa alın ve ısıtın.\r\nİçerisine somonlarınızın derili kısımlarını yerleştirin ve hafif çıtırlaşana kadar pişirin.\r\nArdından etli kısımlarını hazırladığınız ekmek kırıntılı harca batırın.\r\nCam fırın kabınızın içerisine somonlarınızı ve kenarlarına ortadan ikiye böldüğününüz domatesleri yerleştirin.\r\n180 derecede önceden ısıtılmış fırında 20 dakika pişirin.\r\n\r\nPesto sos için;\r\nFesleğen, maydanoz, fındık, sarımsak, tuz ve karabiberi parçalayıcının içerisine alın ve iyice çekin.\r\nArdından parçalayıcının üst haznesinden toz parmesanı ekleyin ve çekin.\r\nZeytinyağını yine üst hazneden yavaş yavaş ekleyin ve her şey birbiriyle iyice karışana kadar çekmeye devam edin.\r\n\r\nBirleştirmek için;\r\nPişen somonunuzu servis edeceğiniz tabağa alın ve yanına pişirdiğiniz domatesleri ilave edin.\r\nSomonunuzun üzerine pesto sos gezdirerek servis edin." },
              new Recipe { Id = 18, Owner = "Isaac", OwnerMail = "isaac@gmail.com", RecipeMaking = "Ayıklanmış hamsilerin orta kılçıklarını çıkartın.\r\nBol suda iyice yıkayıp süzgece alın ve fazla suyunun süzülmesini sağlayın.\r\n\r\nPilav için;\r\nKuş üzümünü bir kaseye alın ve üzerine sıcak su ilave edip bekletin.\r\nPilav tenceresine zeytinyağını koyun.\r\nİnce yemeklik doğranmış soğanları ilave edip yumuşayana kadar soteleyin.\r\nPirinci ilave edip kavurmaya devam edin.\r\n\r\nTuz, karabiber, kuru nane, yenibahar, suyunu süzdüğünüz kuş üzümü, toz şeker ve defne yaprağını ilave edip bir iki tur karıştırın.\r\nSuyunu ilave edin ve tencerenizin kapağını kapatıp kısık ateşte pişmeye bırakın.\r\nDereotunu ince ince kıyın ve pişen pilava ilave edip bir tur çevirin.\r\nDemlenmeye bırakın.\r\nDefne yaprağını pilavdan çıkartın.\r\n\r\n30 cm. çapında yuvarlak alüminyum kabınızı tereyağı ile yağlayın.\r\nHamsilerin derili kısımları alta gelecek şekilde sırt sırta dizin, hafif birbirlerinin üzerine gelmesine dikkat edin.\r\nKabınızın taban kısmını da hamsiyle iyice kapatın.\r\nTuz ve karabiber serpiştirin.\r\nİçine hazırladığınız pilavdan koyun ve kenarlardan sarkan hamsilerle iyice kapatın.\r\nPilavın üzerini de hamsiyle kapatın.\r\nHamsilerin üzerine tuz, karabiber serpin ve limon dilimlerini dizin.\r\n\r\nÜzerine küçük parçalar halinde tereyağı bırakın ve 180 derecede önceden ısıtılmış fırında 30 – 35 dakika üzeri kızarana kadar pişirin." },
              new Recipe { Id = 19, Owner = "Kahn", OwnerMail = "kahn@gmail.com", RecipeMaking = "Somon için;\r\n1 adet haşlanmış pancarı rendeleyin.\r\nSuyunu bir tülbent yardımıyla sıkın, posasını atın, suyunu karıştırma kabına ilave edin.\r\nÜzerine toz şeker ve tuzu ilave edin.\r\nLimonun kabuğunu rendeleyin.\r\nLimonun suyunu sıkın.\r\nRezenenin kök kısmını ince ince doğrayın.\r\nSomonunuzu yıkayın ve kurulayın.\r\nSomonunuzu küçük küp olacak şekilde doğrayın.\r\nSebzelerin olduğu karıştırma kasesine somonları ekleyin, karıştırın ve 15 dakika bekletin.\r\n\r\nSalata için;\r\nKinoanınızı bir sos tenceresine ekleyin ve üzerine suyunu ilave edip, kaynatın.\r\nKırmızı soğanı ince doğrayın.\r\nSalatalığın çekirdeklerini çıkartın, küçük küp doğrayın.\r\nBiberi küçük küp doğrayın.\r\nDomateslerin çekirdeklerini çıkartın ve küçük küp doğrayın.\r\nKereviz sapını ince ince doğrayın.\r\nMangonuzdan bir dilim kesin ve kabuklarını soyun.\r\nArdından küçük küp olacak şekilde doğrayın.\r\nNanelerinizi ince ince doğrayın.\r\nHepsini bir karıştırma kabına toplayın.\r\nHaşlanan kinoanızı salataya ekleyin.\r\nArdından lor peynirini de ekleyin ve karıştırın.\r\n\r\nSos için;\r\nBir karıştırma kabına toz şeker ve limon suyunu çırpın.\r\nŞeker hafif eriyince zeytinyağını ekleyerek çırpmaya devam edin.\r\n\r\nAvokado için;\r\nAvokadonuzun kabuğunu soyun.\r\nKüçük küpler halinde doğrayın.\r\nHazırladığınız sostan 2 yemek kaşığı avokadonuzun üzerine gezdirin." },
              new Recipe { Id = 20, Owner = "Lionel", OwnerMail = "lionel@gmail.com", RecipeMaking = "Karidesleri, ince bir fileto ya da carving bıçağı ile karın kısımlarındaki zarı , içindeki etli bölümü zedelemeden keserek işe başlayın. Kuyruga kadar itina ile zarı kestiginize emin olun Daha sonra işaret parmagınız ile kafasının arkasındaki kabugun kafaya birleşme yerinden , dış kabugu çıtlatın. Parmağınız ile bütün sırtındaki kabuk boyunca , kuyruguna kadar kabugu zedelemeden aşağı kadar inerek kabugu koparmadan etli yuzeyden ayırın. Aynı fileto ya da carving bıçağı ile karidesleri sırt kısmına kafadan başlayıp kuyruga kadar bir derin çizik atın. Karidesin iç organları ve pisligi bu kısımda bulunur ve bu kısmın mutlaka cıkartılması gerekir. Bu kısmı çıkarttıktan sonra kafa kısmındaki diger organlarını da bir çay kaşığı ile temizleyin Bütün karidesi yıkayın ve tekrar kabuğunu etli kısmın üstüne kapatın Bir tavaya kaya tuzunu bir kat olarak serin ve altını harlı ateşte ısıtmaya başlayın. Tuz ısınınca karidesleri (kabukları üzerinde olacak) tuzun üstüne yerleştirin her iki tarafını da cevirerek, 5-6 dakika kadar pişirin İstediğiniz bahçe yeşillikleri üzerinde kabuğu ile servis edin." },
              new Recipe { Id = 21, Owner = "Staphen", OwnerMail = "staphen@gmail.com", RecipeMaking = "Asma yapraklarını salamura kullanıyorsanız, sadece sıcak suda bekletip tuzunu alın.\r\nTaze asma yaprağı kullanıyorsanız, suda yıkayıp haşlayın.\r\n\r\nPilav için;\r\nPirinci nişastası çıkıncaya kadar yıkayıp süzün.\r\nGeniş bir tencereyi ocağa alın, içerisine zeytinyağını alın ve kızdırın.\r\nDolmalık fıstığı, tencereye ekleyip hafif renk alana kadar kavurun, soğanı ilave ederek kavurmaya devam edin.\r\nSoğanlar yumuşayınca yıkanmış pirinci ekleyin, şeffaflaşana kadar kavurmaya devam edin.\r\nTuz, karabiber, tarçın, yenibahar ve kuru naneyi ekleyip karıştırın.\r\nPirincin üzerine 1 su bardağı vişne suyu, 1 su bardağı su, 1 yemek kaşığı nar ekşisi, 1 yemek kaşığı toz şeker ve kuş üzümünü ekleyin, bir tur karıştırın.\r\n\r\nPilavın suyu kaynadıktan 5 dakika sonra ocaktan alın ve soğumaya bırakın.\r\n\r\nSarmak için;\r\nTencerenin dibine asma yapraklarından bir taban yapın.\r\nAsma yapraklarına uygun miktarda harç ve birer adet vişne koyup, kenarlarını ortaya katlayarak parmak kalınlığında sarın.\r\nHazırladığınız sarmaları, asma yapraklarının üzerine dizin.\r\nKalan vişneleri aralarına serpiştirin.\r\nÜzerine 1 – 1,5 su bardağı su, vişne suyu ve 4 – 5 yemek kaşığı zeytinyağı ekleyin.\r\nÜzerlerine porselen bir tabak koyup tencerenin kapağını kapatın.\r\nÇok kısık ateşte yaklaşık 35 – 40 dakika pişirin.\r\n\r\nKendi tenceresinde oda sıcaklığına gelmesini bekleyin.\r\nIlık ya da soğuk servis edin." },
              new Recipe { Id = 22, Owner = "Leo", OwnerMail = "leo@gmail.com", RecipeMaking = "Bir karıştırma kabına su ve un ilave edip ayıkladığınız baklaları içerisine alın.\r\nDiğer malzemeler hazırlanana kadar bekletin.\r\nTencerenizi ocağa alın ve ısıtın.\r\nIsınan tencerenin içerisine zeytinyağı ince yemeklik doğradığınız soğanları ilave edip yumuşayana kadar kavurun.\r\nKavrulan soğanın içerisine suda beklettiğiniz baklaları ilave edin.\r\nTuz, karabiber, toz şeker ve un serpip karıştırın.\r\nArdından suyunu ilave edin ve orta ateşte 20 dakika pişirin.\r\nPişen baklanızı ocaktan alın ve tencerede soğumaya bırakın.\r\nSoğuyan baklanızı servis tabağına alın ve üzerine ince doğradığınız dereotu serpin.\r\nYanına yoğurtla servis edin." },
              new Recipe { Id = 23, Owner = "Jack", OwnerMail = "jack@gmail.com", RecipeMaking = "Soğanlarınızı küçük küpler olacak şekilde doğrayın.\r\nGeniş pilav tencerenizi ocağa alın ve ısıtın.\r\nZeytinyağını ilave edip soğanlar yumuşayana kadar pişirin.\r\nSoğanlarınız hafif yumuşayınca iç baklaları tencereye ilave edin ve hafif soteleyin.\r\nİçerisine sarımsak, tuz, beyaz biber ve toz şekeri ilave edin.\r\nEnginarları soğanlı harcınızın üzerine dizin.\r\nLimon suyu ve suyu ilave edip kısık ateşte pişmeye bırakın.\r\nPişen iç baklalı enginarınızı ocaktan alın ve tencerede hafif soğutun.\r\nHazır olan enginarlarınızı servis edeceğiniz tabağa alın.\r\nDereotlarınızı ince kıyın ve soğanlı ve iç baklalı harcın içerisine alıp karıştırın.\r\nEnginarların iç kısımlarına soğanlı ve iç baklalı harçtan doldurun.\r\nÜzerinden suyunu ve zeytinyağını gezdirin." },
              new Recipe { Id = 24, Owner = "Mahi", OwnerMail = "mahi@gmail.com", RecipeMaking = "Baklayı 1 gece önceden soğuk suda ıslatın.\r\nSoğanı iri iri doğrayın. Bir tencereye zeytinyağını ekle ve ısınınca soğanları ekleyip biraz kavurun, ardından bakla, şeker, tuz, dereotunun yarısı, üzerini 4 parmak geçecek kadar su koyarak tüm malzemeler iyice yumuşayıncaya ve içerisinde hiç sıvı kalmayıncaya kadar pişirin.\r\nFavanın çok sulu olmaması için suyunu çekmesi gerekir. Tenceredeki malzeme lapa kıvamında olmalıdır. Hazır hale geldiğinde el mikseriyle bütün malzemeyi çekin ve soğuması için kenara alın.\r\n\r\nEnginar için;\r\n\r\nSoğanı yemeklik doğrayın. Tencereye zeytinyağını ilave edin ısınınca soğanları ekleyin ve kavurun.\r\nSoğanlar yumuşayınca enginarları ekleyin. Bir portakalın suyunu ve içindeki parçacıklarını ekleyin. Tuz, şeker ve su ekleyip kapağını kapatın. Enginarlar pişince tencereyi kenara alın.\r\n\r\nEnginarlar soğuyunca içlerine fava doldun. Enginarın içinde piştiği portakallı sostan üzerlerine gezdirin ve dereotu yaprakları serperek servis edin." },
              new Recipe { Id = 25, Owner = "Sevinç", OwnerMail = "sevinc@gmail.com", RecipeMaking = "Patlıcanlar için;\r\nPatlıcanlarınızın baş ve uç kısımlarını kesin.\r\nOrtadan ikiye bölün ve kızgın zeytinyağında patlıcanları bütün olarak önlü arkalı yumuşayıncaya kadar kızartın.\r\nHepsini kızarttıktan sonra kağıt havlunun üzerine alın.\r\n\r\nİç harcı için;\r\nSoğanları piyazlık doğrayın.\r\nSarımsakları boyundan ikiye bölün.\r\nBiberleri ince doğrayın.\r\nTavanızı ocağa alın, zeytinyağını ekleyip ısıtın.\r\nIsınan yağınızın içerisine soğanınızı ekleyin ve hafif yumuşayana kadar soteleyin.\r\nSotelenen soğanınızın içerisine sarımsak, biber ve sebzelerinizin kolay sotelenmesi için birazcık tuz ekleyip sotelemeye devam edin.\r\nDomateslerinizin kabuklarını soyun.\r\nDomateslerinizin çekirdeklerini çıkartın ve küçük küpler halinde doğrayın.\r\nSoğanlarınıza tuz ve karabiberi ekleyip karıştırın.\r\nDomatesi ekleyin ve sotelemeye devam edin.\r\nToz şekeri içerisine ekleyin ve karıştırın.\r\nMaydanozu ince ince doğrayın ve sotelenen sebzelerinizin içerisine alıp ocaktan alın.\r\n\r\nBirleştirmek için;\r\nPatlıcanları tencereye dizin.\r\nOrtalarını, bıçak yardımıyla çizgi şeklinde açın, kaşıkla genişletin, tabana kadar inmemeye özen gösterin.\r\nİçlerine biraz toz şeker, tuz ve karabiber serpip hazırladığınız harçtan doldurun.\r\nTavada kalan domatesli suyu (domatesin suyu kalmadıysa 2-3 yemek kaşığı suyu) tencerenin üzerine dökün.\r\nÜzerine zeytinyağı ve su gezdirin.\r\nKapağı kapalı olarak 20 – 25 dakika pişirin.\r\n\r\nNot: Eğer kızartma yapmak istemezseniz, her şeyi çiğ olarak, aynı metotla, pişirme süresini 1 saat civarında arttırarak yapabilirsiniz." },
              new Recipe { Id = 26, Owner = "Mutlu", OwnerMail = "mutlu@gmail.com", RecipeMaking = "Nohudu bir karıştırma kabının içerisine alın ve üzerine su ilave edip 1 gece bekletin.\r\nParçalayıcınızın haznesine beklettiğiniz nohutları alın.\r\nÜzerine kabaca doğradığınız soğan, sarımsak, maydanoz ve kişnişlerin yaprak kısımlarını alın.\r\nTuz, karabiber, kimyon ve pul biberi ilave edip her şeyi biraz pütürlü kalacak şekilde parçalayıcıdan geçirin.\r\nNOT : Tamamen püre olmamasına dikkat edin.\r\nBu karışıma 2 – 3 yemek kaşığı un ekleyin ve yoğurun.\r\nElde ettiğiniz karışımdan ceviz büyüklüğünde toplar yapın.\r\nTavanızı ocağa alın ve ayçiçek yağı ilave edip ısıtın.\r\nIsınan yağın içerisine falafelleri alın ve kızartın.\r\nKızaran falafelleri bir havlu kağıdın üzerine çıkartın ve fazla yağından süzdürün.\r\n\r\nSosu için;\r\nBir karıştırma kabının içerisinde süzme yoğurt, tahin ve limon suyunu karıştırın.\r\nZeytinyağını ilave edip karıştırmaya devam edin.\r\n\r\nServis etmek için;\r\nDomateslerinizi ortadan ikiye bölün ve bir karıştırma kabına alın.\r\nÜzerine bebek roka, tuz, karabiber ve zeytinyağı gezdirip harmanlayın.\r\nHazırladığınız salatayı servis edeceğiniz tabağın tabanına alın.\r\nOrta kısmını hafif boş bırakın ve hazırladığınız yoğurtlu sosu salatanızın ortasına yerleştirin.\r\nYoğurdun üzerine ve kenarlarına kızarttığınız falafelleri yerleştirin." },
              new Recipe { Id = 27, Owner = "Kadir", OwnerMail = "kadir@gmail.com", RecipeMaking = "Tavanızı ocağa alın ve ısıtın.\r\nIsınan tavanızın içerisine bademlerinizi alın ve renk alana kadar kavurun.\r\nSemizotlarınızı yapraklarını ayırın ve yıkayıp fazla suyundan arındırın.\r\nSemizotlarınızı bir karıştırma kabına alın ve üzerine çok hafif zeytinyağı, tuz ve karabiber ilave edip iyice harmanlayın.\r\nŞeftalinizi elma dilim olacak şekilde kesin.\r\nİzli tavanızı ocağa alın ve ısıtın.\r\nIsınan izli tavanızın içerisine dilimlediğiniz şeftalileri alın ve iz alana kadar pişirin.\r\nSemizotlarınızın içerisine elinizle parçaladığınız keçi büş peynirini ve kavurduğunuz bademleri ilave edip harmanlayın.\r\nServis tabağınızın kenarlarına iz verdirdiğiniz şeftalileri dizin.\r\nSemizotunu şeftalilerin ortasına yerleştirin.\r\nHafif zeytinyağı gezdirerek servis edin." },
              new Recipe { Id = 28, Owner = "İsa", OwnerMail = "isa@gmail.com", RecipeMaking = "Sultaniye üzümlerini sıcak suda bekletin.\r\nMor lahananızı ortadan ikiye kesin.\r\nKök kısmı altta kalacak şekilde lahananızı tezgaha koyun ve ince ince dilimleyin.\r\nDilimlediğiniz lahanayı bir karıştırma kabına alın ve 1 yemek kaşığı tuzla iyice ovun.\r\nHavucu rendeleyin.\r\nSalatalıkların sadece dış taraflarını rendeleyin, çekirdekli kısımlarını rendelemeyin.\r\nBir tülbent yardımıyla lahana, salatalık ve havucun suyunu sıkın.\r\nKırmızı soğanı ufak ufak doğrayın.\r\nLahana ve salatalığı bir tülbent yardımıyla iyice sıkın.\r\nLime’nin kabuğunu rendeleyin.\r\nKişnişi saplarından ayırın ve ince kıyın.\r\nCevizi iri kıyın.\r\nSultaniye üzümün suyunu süzün.\r\nDoğradığınız her şeyi bir karıştırma kabında karıştırın.\r\n\r\nSosu için;\r\nSos için olan tüm malzemeleri karıştırın ve hazırladığınız salatayla karıştırıp, servis edin." },
              new Recipe { Id = 29, Owner = "Musa", OwnerMail = "musa@gmail.com", RecipeMaking = "Tüm malzemeleri dilediğiniz gibi doğrayıp bir kâseye alın.\r\n\r\nSos için yoğurt, limon suyu, tuz ve zeytinyağını karıştırın ve salatayla birleştirip servis edin." },
              new Recipe { Id = 30, Owner = "Murat", OwnerMail = "murat@gmail.com", RecipeMaking = "Ispanak kökünü diri kalacak şekilde tuzlu suda haşlayın ve buzlu suyun içine çıkararak şoklayın.\r\n\r\nBir kâsede ezilmiş sarımsak, süzme yoğurt, limon suyu, tuz, karabiber, pul biber ve zeytinyağını çırpın.\r\n\r\nIspanak köklerini servis kâsesine alın. Üzerine hazırladığınız sostan gezdirin ve küçük doğradığınız domatesleri serpiştirerek servis edin." },
              new Recipe { Id = 31, Owner = "Cenk", OwnerMail = "cenk@gmail.com", RecipeMaking = "Tabanı için;\r\nBir karıştırma kabının içerisine granül kahve, sıcak su ve vanilya özütünü alıp karıştırın.\r\nKedi dillerinizi hazırladığınız sıvı karışımın içerisine batırıp çıkartın ve servis edeceğiniz kuplara dikine yerleştirin.\r\n\r\nArası için;\r\nMuzlarınızı kabuklarıyla birlikte dikine ortadan ikiye bölün.\r\nİzli tavanızı ocağa alın ve ısıtın.\r\nİkiye böldüğünüz muzları ısınan tavanıza alın ve iyice karamelize olana kadar pişirin.\r\nPişen muzlarınızı tezgaha alın ve doğrayın.\r\nKuplarınızın tabanına doğradığınız muzları ilave edin.\r\n\r\nMuhallebi için;\r\nBir tencerede tereyağını eritin.\r\nTereyağı eriyince unu ilave edip renk alana kadar kavurun.\r\nArdından sütü ilave edip sürekli karıştırarak kaynatın.\r\nSüt kaynayınca şeker ve dövülmüş damla sakızını ilave edin, iyice karıştırın, kaynamaya başlayınca altını kapatın.\r\nHazır olan muhallebinizi kuplarınıza pay edin.\r\nÖnce oda sıcaklığında ardından buzdolabında soğutun.\r\nSoğuttuğunuz muhallebinizin üzerine kakao serperek servis edin." },
              new Recipe { Id = 32, Owner = "Ceren", OwnerMail = "ceren@gmail.com", RecipeMaking = "Damla sakızını buzlukta soğuttuktan sonra bir dövücü yardımıyla toz haline getirin.\r\nPirinci yıkayın, süzün.\r\nKüçük sos tenceresinin içerisine 2,5 su bardağı süt ve pirinci ilave edin.\r\nKısık ateşte pirinçler yumuşayıncaya kadar haşlayın.\r\nKalan sütü tencereye ilave edin.\r\nNişasta, pirinç unu, toz şeker ve damla sakızını karıştırın.\r\nHaşladığınız pirinçleri bu karışıma ilave edin.\r\nSürekli karıştırarak kıvam alıncaya kadar pişirin.\r\nKıvam alan sütlaçlarınızı güveçlerinize pay edin.\r\nSütlaçlarınızı bir fırın tepsisine alın, tabanına az su koyun ve 180 derecede önceden ısıtılmış fırında üzeri kızarana kadar pişirin." },
              new Recipe { Id = 33, Owner = "Cemile", OwnerMail = "cemile@gmail.com", RecipeMaking = "Sosu için;\r\nTüm malzemeleri sos tenceresine ilave edip, karıştırın ardından ateşin üzerine alıp kaynayıncaya kadar karıştırarak pişirin.\r\nKenara alıp soğutun.\r\n\r\nKeki için;\r\nToz şeker ve yumurtaları rengi açılıncaya kadar çırpın.\r\nArdından sıvı yağ ve sütü ilave edip, çırpmaya devam edin.\r\nUn, kakao, kabartma tozu, vanilya ve limon kabuğunu ilave edip spatula ile karıştırın.\r\nKek hamurunu yağlanmış kek kalıbına dökün 180 derecede önceden ısıtılmış fırında 30 dakika pişirin.\r\n\r\nNot: Kullandığınız kek kalıbının genişlik ve derinliğine göre pişirme süresine dikkat ediniz. Biraz daha az veya fazla olabilir. Tarifte 30 cm. çaplı kalıp kullanılmıştır.\r\n\r\nKekiniz fırından çıktıktan sonra 5 dakika dinlendirip, dilimleyin.\r\nSoğuyan sosu kekin üzerine dökün ve soğuyuncaya kadar bekleyin.\r\nArdından servis edin." },
              new Recipe { Id = 34, Owner = "Atatürk", OwnerMail = "atatürk@gmail.com", RecipeMaking = "Kek için;\r\nTezgah üstü mikserinizi tezgahınızın üzerine alın.\r\nHaznesinin içerisine toz şeker, un, kakao, kabartma tozu, kabartma tozu, karbonat ve vanilyayı ekleyip her şeyi düşük devirde karıştırın.\r\nBir karıştırma kabında sıcak su ve granül kahveyi karıştırıp kuru malzemelerinizin içerisine ekleyin.\r\nArdından ayçiçek yağı, süt, yumurta ve tuzu ekleyip yüksek devirde her şey karışana kadar karıştırın.\r\n\r\nDikdörtgen fırın kabınızın tabanına fırın kağıdı serin, kenarlarını yağlayın.\r\nKek hamurunuz hazır olunca fırın kabınızın içerisine dökün ve 180 derecede önceden ısıtılmış fırında 25 dakika pişirin.\r\nKekiniz pişince soğumaya bırakın.\r\n\r\nKreması için;\r\nBir karıştırma kabının içerisine labneyi ekleyin.\r\nÜzerine pudra şekerini ilave edin ve bir spatula yardımıyla çok sulandırmadan karıştırın.\r\nHazırladığınız kremayı ucuna tırtıklı bir uç taktığınız sıkma torbasının içerisine ekleyin.\r\n\r\nBirleştirmek için;\r\nHafif soğuyan kekinizi iyice soğutmak için fırın kabından çıkartın ve ters çevirerek başka bir yağlı kağıdın üzerine alın.\r\nSoğuttuğunuz kekinizden 7 cm’lik bir çember yardımıyla küçük yuvarlaklar çıkartın.\r\nHazır olan keklerinizin tabanı olacak olan keklerinize hazırladığınız kremadan sıkın.\r\nArdından ince dilimlediğiniz çileklerinizi kremanın üzerine dizin.\r\nDiğer kekinizi üzerine yerleştirin.\r\nPastanızın üzerini dilediğiniz şekilde süsleyin." },
              new Recipe { Id = 35, Owner = "Metehan", OwnerMail = "metehan@gmail.com", RecipeMaking = "Pandispanya için;\r\nYumurta ve yumurta sarılarını toz şekerle birlikte iyice kıvam alıncaya kadar çırpın.\r\nAyrı bir kapta un, vanilya, kabartma tozu ve nişastayı eleyip yumurtalı karışıma spatula yardımı ile içten dışa doğru karıştırarak yedirin.\r\nLimon kabuğunu rendeleyin ve karışıma iyi yedirin\r\nAyrı bir kapta yumurta aklarını 1 yemek kaşığı toz şekerle beze kıvamına gelinceye kadar iyice çırpın ve unlu karışıma, bir spatula yardımı ile hamuru söndürmeden dikkatlice içten dışa doğru karıştırarak yedirin.\r\nFırın tepsinize yağlı kağıt serin, kaymaması için tabanını yağlayabilirsiniz ve pandispanya hamurunu tepsiye döküp, spatula yardımı ile dikdörtgen olacak şekilde yayın.\r\n220 – 230 derecede önceden ısıtılmış fırında 8 – 10 dakika pişirin.\r\nPandispanya fırından çıkmasına yakın tezgahınıza fırın kağıdı serip üzerine elekle pudra şekeri serpin.\r\nPandispanya fırından çıkınca ters çevirerek pudra şekeri serpişmiş kağıdın üzerine yerleştirin. Üstte kalan fırın kağıdını dikkatlice sıyırın ve pandispanyayı rulo yapıp kenara alın.\r\n\r\nLabneli katman için;\r\nDonuk meyve kullanıyorsanız bir eleğe alın ve bir kabın içerisine oturtun.\r\nSuyunu salması için kenara alın.\r\nAyrı bir kapta labne peynirini ve pudra şekeri bir spatula yardımıyla iyice karıştırın.\r\nArdından meyveler suyunu iyice süzünce hafif ezin ve peynirin içerisine alıp karıştırın.\r\nKarışım homojen bir kıvam alınca kenara alın.\r\nPandispanyanın üzerine labneli harcı yayın, rulo yapacağınız uç kısma kalan meyve tanelerini dizin ve rulo yapın.\r\n\r\nGanaj için;\r\nÇikolatayı benmari olacak şekilde eritin.\r\nİçerisine kremayı ekleyin ve ocaktan alıp karıştırın.\r\n\r\nBirleştirmek için;\r\nRulo pastayı bir telin üzerine alın ve üzerine hazırladığınız ganajı gezdirin.\r\nServis edeceğiniz tabağa alın ve üzerlerini vişne ve toz antep fıstığıyla süsleyerek servis edin." },
              new Recipe { Id = 36, Owner = "Oğulcan", OwnerMail = "ogulcan@gmail.com", RecipeMaking = "Tüm malzemeleri blendere alın, karıştırın ve soğuk servis edin." },
              new Recipe { Id = 37, Owner = "Berkay", OwnerMail = "berkan@gmail.com", RecipeMaking = "Limon ve portakalların kabuklarını bir kaba rendeleyin.\r\n\r\nÜzerine şeker ilave edin elinizle iyice ovun.\r\n\r\nArdından üzerine sıcak su döküp şeker eriyinceye kadar karıştırın.\r\n\r\nKabuklarını rendelediğiniz portakal ve limonların suyunu sıkıp şekerli karışıma ilave edin.\r\n\r\nLimonata özünü süzgeçten geçirin ve buzdolabında soğuttuktan sonra üzerine su ve nane yaprakları ilave edip servis edin." },
              new Recipe { Id = 38, Owner = "Kanuni", OwnerMail = "kanuni@gmail.com", RecipeMaking = "Tüm malzemeleri bir tencerenin içerisine alıp sürekli karıştırarak kaynatın ve kıvam aldıktan sonra bardaklara bölüştürüp, üzerine tarçın serperek servis edin." },
              new Recipe { Id = 39, Owner = "Sherlock", OwnerMail = "sherlock@gmail.com", RecipeMaking = "Fındığınızı çok toz haline gelmeden hafif çekin.\r\nMuzunuzu elinizle bölün ve bir karıştırma kabının içerisine alın.\r\nÜzerine fındıkları ekleyin ve bir parçalayıcı yardımıyla karıştırın.\r\nİçerisine bisküviyi alın ve karıştırmaya devam edin.\r\nHazırladığınız karışımın içerisine sütü ekleyin ve parçalayıcı yardımıyla karıştırmaya devam edin.\r\nHazırladığınız milkshake’inizi buzdolabına koyun ve soğutun.\r\nSoğuyan milkshake’inizi bardaklara pay edip, servis edin." },
              new Recipe { Id = 40, Owner = "Grimm", OwnerMail = "grimm@gmail.com", RecipeMaking = "Bir sürahiye 1 litre kaynamış su ekleyin.\r\nİçerisine 3 adet poşet siyah çay atın ve demlenmeye bırakın.\r\n2 adet limonun kabuğunu ince uzun parçalar olacak şekilde rendeleyin.\r\nArdından rendelediğiniz limonu ince dilimler olacak şekilde kesin.\r\nÇayınız demlenince poşet çayları içerisinden çıkarın.\r\nÇayınız hazır sıcakken içerisine rendelediğiniz limon kabuklarını ve limon dilimlerini ekleyin. Toz şekeri ve karanfili ilave edin, şeker tamamen eriyene kadar karıştırın.\r\nŞeker eriyince taze naneleri ekleyin.\r\nArdından yarım litre oda sıcaklığında su ve limon suyunu ekleyin.\r\nNot: Limon suyunu en son koymalısınız, yoksa buzlu çayınız biraz acımtırak olacaktır. Soğuması için biraz kenarda bekletin.\r\nArdından buzdolabına koyun ve soğumaya bırakın.\r\n\r\nNot: 2 – 3 gün buzdolabında saklayabilirsiniz!!" }


               );
        }
    }
}
